USE WIPRO_MSDYNAMICS;-- table creation CREATE TABLE Students1 (    StudentID INT IDENTITY PRIMARY KEY,    Name VARCHAR(50),    Age INT,    Course VARCHAR(50));-- insertion valuesINSERT INTO Students1 (Name, Age, Course)VALUES ('Ravi', 20, 'Computer Science');INSERT INTO Students1 (Name, Age, Course)VALUES ('Priya', 22, 'Mathematics');INSERT INTO Students1 (Name, Age, Course)VALUES ('Arjun', 21, 'Physics');-- retrieve dataSELECT * FROM Students1;--Select Specific ColumnsSELECT Name, Course FROM Students1;--Filter Records (WHERE)SELECT * FROM Students1WHERE Age > 20;--Update a RecordUPDATE Students1SET Course = 'Data Science'WHERE StudentID = 2;--Delete a RecordDELETE FROM Students1WHERE StudentID = 3;-- create a new table that consists of constraintsCREATE TABLE EMPLOYEE(     EmpId INT PRIMARY KEY,     EmpName VARCHAR(100) UNIQUE,     EmpAge INT NOT NULL,     EmpSal INT CHECK(EmpSal > 5000));-- insert valuesINSERT INTO EMPLOYEE (EmpId, EmpName, EmpAge, EmpSal)VALUES (1, 'Ravi Kumar', 28, 25000);INSERT INTO EMPLOYEE (EmpId, EmpName, EmpAge, EmpSal)VALUES (2, 'Priya Sharma', 32, 32000);INSERT INTO EMPLOYEE (EmpId, EmpName, EmpAge, EmpSal)VALUES (3, 'Arjun Reddy', 26, 18000);INSERT INTO EMPLOYEE (EmpId, EmpName, EmpAge, EmpSal)VALUES (4, 'Meena Gupta', 40, 45000);INSERT INTO EMPLOYEE (EmpId, EmpName, EmpAge, EmpSal)VALUES (5, 'Suresh Das', 35, 60000);--  select SELECT * FROM EMPLOYEE;-- UPDATEUPDATE EMPLOYEE SET EmpAge = 33 WHERE EmpId = 4;-- DELETE DELETE FROM EMPLOYEE WHERE EmpAge >= 35;